import axios from "axios";

const Post = props => (
  <div>
    <h1>Comments for posts #{props.id}</h1>
  </div>
);

Post.getInitialProps = async ({ query }) => {
  const res = await axios.get("https://jsonplaceholder.typicode.com/comments?postId=3");
  const data = res.data;
  return { ...query, comments:data };
};
export default Post;
